function Saudacao ({nome, setNome}) {
    setNome('')
    alert(nome)
    setNome(nome.toLowerCase()===nome.toUpperCase()?null:nome)
    function gerarSaudacao (algumNome){
        return `Seu nome é ${algumNome}, certo?`
    }
    

    return (
        <>
            {nome && <h1>{gerarSaudacao(nome)}</h1>}
        </>
    )
} 

export default Saudacao
